class Results extends React.Component {

	constructor(props) {
		super(props)
		this.state = {
			itemDisplayLimit: 5,
			currentDisplayCount: 0,
			dataFromDb: [],
		}
	}

	onReceiveScholarships = (data) => {
		debugger;
	}
	componentDidMount() {
		/*$.ajax({
			url: "http://127.0.0.1:5000",
			success: this.onReceiveScholarships
		});*/
		this.setState({
			dataFromDb: dataFromDb
		})
	}

	handleEnter = (index) => (event) => {
		mockScholarships[index].finishedAnimation = true
		this.setState(this.state)
	}

	hasSpace = (index) => {
		return (this.state.currentDisplayCount <= this.state.itemDisplayLimit) 
	}

	passes(scholarship) {
		let result = false;
		let thingsToCheck = [
			scholarship.school, 
			scholarship.award_name,
			scholarship.link, 
			scholarship.amount, 
			scholarship.deadline, 
			scholarship.field_of_study
		];
		result = thingsToCheck.filter(e => e.toLowerCase().includes(this.props.query.toLowerCase())).length > 0;


		if (this.props.filters && result) {
			let f = this.props.filters;
			if (f.Title) {
				result = result && scholarship
					.award_name
					.toLowerCase()
					.includes(f.Title.toLowerCase());
			}
		}
		return result;
	}

	render(){
		let gridElements = [];
		this.state.currentDisplayCount = 0
		for (let i = 0; i < this.state.dataFromDb.length && this.hasSpace(i); i++) {
			let element = this.state.dataFromDb[i];
			if (this.passes(element)) {
				this.state.currentDisplayCount++;
				gridElements.push(
					<Grid item xs={3}>
						<Zoom in={true} onEntered={this.handleEnter(i)} timeout={100}>
							<Paper>
								<div>
									<Typography variant="h6">
										{element.award_name}
									</Typography>
								</div>
								<div>
									<Typography variant="subtitle1">
										{element.field_of_study}
									</Typography>
								</div>
								<div>
									<Typography variant="subtitle2">
										{element.amount}
									</Typography>
								</div>
							</Paper>
						</Zoom>
					</Grid>
				);
			}
		}
		return (
			<Container maxWidth="sm">
				<Grid container spacing={3}>
					{gridElements}
				</Grid>
			</Container>
		);
	};
}
export default Results;

